<script setup>
const supabase = useSupabaseClient();
const nama = ref("");
const desc = ref("");
const alert = ref(false)

const PostMasukan = async () => {
  alert.value = false
  const { data, error } = await supabase.from("masukan").insert({
    nama: nama.value,
    desc: desc.value,
  });
  if (error) {
    console.log(error);
  }
  else{
    // alert("Berhasil Mengirim Masukan")
    nama.value = ""
    desc.value = ""
    alert.value = true
  }
};
</script>
